/**
 * @brief _homepage
 */
void uniT::_homepage() {
    MainWindow main(nullptr);
    HomePage home(nullptr);
//    QCOMPARE(Home.logined,true);
    QCOMPARE(home.logindialog->isVisible(), false);
    mouseClick(home.avatar, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), true);
    mouseClick(home.logindialog->quit, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), false);
    QCOMPARE(home.logindialog->isVisible(), false);
    mouseClick(home.pvp, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), true);
    mouseClick(home.logindialog->quit, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), false);
    QCOMPARE(home.logindialog->isVisible(), false);
    mouseClick(home.line, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), true);
    mouseClick(home.logindialog->quit, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), false);
    QCOMPARE(home.logindialog->isVisible(), false);
    mouseClick(home.text, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), true);
    mouseClick(home.logindialog->quit, Qt::LeftButton);
    QCOMPARE(home.logindialog->isVisible(), false);
    QCOMPARE(home.helpText->isVisible(), false);
    mouseClick(home.help, Qt::LeftButton);
    QCOMPARE(home.helpText->isVisible(), true);
    mouseClick(home.helpText->close, Qt::LeftButton);
    QCOMPARE(home.helpText->isVisible(), false);
}

